public List<Integer> findDivisibleNumbers(Integer x, Integer y, Integer n) {
        List<Integer> divisibleNumbers = new List<Integer>();

        // Check if any parameter is null or if y is less than x
        if (x == null || y == null || n == null || y < x) {
            return divisibleNumbers;
        }

        // Iterate through the range from x to y (inclusive)
        for (Integer i = x; i <= y; i++) {
            // Check if the current number is divisible by n
            if (Math.mod(i, n) == 0) {
                divisibleNumbers.add(i);
            }
        }

        return divisibleNumbers;
    }

