public Boolean marathonDistance(List<Integer> distances) {
       // Check if the list is empty or null
       if (distances == null || distances.isEmpty()) {
           return false;
       }

       // Calculate the total distance
       Integer totalDistance = 0;
       for (Integer distance : distances) {
           // Skip null distances
           if (distance == null) {
               continue;
           }
           // Convert negative distances to positive
           totalDistance += Math.abs(distance);
       }

       // Check if the total distance is at least 15 miles
       return totalDistance == 15;
   }

