public List<Integer> mirror(List<Integer> originalList) {
    // Check if the original list is null or empty
    if (originalList == null || originalList.isEmpty()) {
        return new List<Integer>(); // Return an empty list if originalList is null or empty
    }

    // Create a new list to store the mirrored result
    List<Integer> mirroredList = new List<Integer>();

    // Add all elements from the original list to the mirrored list
    mirroredList.addAll(originalList);

    // Iterate backwards through the original list (excluding the last item)
    for (Integer i = originalList.size() - 2; i >= 0; i--) {
        // Check for null values in the original list and skip them
        if (originalList[i] != null) {
            // Add each element to the mirrored list
            mirroredList.add(originalList[i]);
        }
    }

    return mirroredList;
}

