public Boolean areAnagrams(String str1, String str2) {
        // Check if either string is null or empty
        if (str1 == null || str2 == null || String.isEmpty(str1) || String.isEmpty(str2)) {
            return false;
        }

        // If lengths of both strings are not equal, they cannot be anagrams
        if (str1.length() != str2.length()) {
            return false;
        }

        // Convert strings to char lists
        List<String> charList1 = new List<String>();
        for (Integer i = 0; i < str1.length(); i++) {
            charList1.add(str1.substring(i, i+1));
        }

        List<String> charList2 = new List<String>();
        for (Integer i = 0; i < str2.length(); i++) {
            charList2.add(str2.substring(i, i+1));
        }

        // Sort both char lists
        charList1.sort();
        charList2.sort();

        // Convert char lists back to strings and compare
        String sortedStr1 = String.join(charList1, '');
        String sortedStr2 = String.join(charList2, '');

        return sortedStr1.equals(sortedStr2);
    }

