public class DailyHighValueAccountMarker implements Schedulable {
    public void execute(SchedulableContext context) {
        List<Account> highValueAccounts = [SELECT Id, Description FROM Account WHERE AnnualRevenue > 1000000];
        for (Account account : highValueAccounts) {
            account.Description = 'High Value Account';
        }
        update highValueAccounts;
    }
}

tests:
DailyHighValueAccountMarker job = new DailyHighValueAccountMarker();
job.execute(null);
Assert.isNotNull(job, 'DailyHighValueAccountMarker class must exist with execute method');

Account highValueAcc = new Account(Name = 'TestHighValue_' + DateTime.now().getTime(), AnnualRevenue = 2000000);
insert highValueAcc;
DailyHighValueAccountMarker job = new DailyHighValueAccountMarker();
job.execute(null);
Account result = [SELECT Description FROM Account WHERE Id = :highValueAcc.Id];
delete highValueAcc;
Assert.areEqual('High Value Account', result.Description, 'High value accounts should have Description updated');

Account lowValue = new Account(Name = 'TestLowValue_' + DateTime.now().getTime(), AnnualRevenue = 5000);
insert lowValue;
DailyHighValueAccountMarker job = new DailyHighValueAccountMarker();
job.execute(null);
Account result = [SELECT Description FROM Account WHERE Id = :lowValue.Id];
delete lowValue;
Assert.isNull(result.Description, 'Low value accounts should not be updated');

Account anotherHighValue = new Account(Name = 'TestAnotherHighValue_' + DateTime.now().getTime(), AnnualRevenue = 5000000);
insert anotherHighValue;
DailyHighValueAccountMarker job = new DailyHighValueAccountMarker();
job.execute(null);
Account result = [SELECT Description FROM Account WHERE Id = :anotherHighValue.Id];
delete anotherHighValue;
Assert.areEqual('High Value Account', result.Description, 'All high value accounts should be marked');

