public class DailyOpportunityFollowUp implements Schedulable {
    public void execute(SchedulableContext context) {
        List<Opportunity> opportunities = [SELECT Id, Name, OwnerId FROM Opportunity WHERE StageName = 'Negotiation'];
        List<Task> tasks = new List<Task>();
        for (Opportunity opportunity : opportunities) {
            Task task = new Task();
            task.Subject = 'Follow up: ' + opportunity.Name;
            task.WhatId = opportunity.Id;
            task.OwnerId = opportunity.OwnerId;
            task.ActivityDate = Date.today().addDays(1);
            tasks.add(task);
        }
        insert tasks;
    }
}

tests:
DailyOpportunityFollowUp job = new DailyOpportunityFollowUp();
job.execute(null);
Assert.isNotNull(job, 'DailyOpportunityFollowUp class must exist with execute method');

Account account = new Account(Name = 'Test Account For FollowUp');
insert account;
Opportunity opportunity = new Opportunity(Name = 'Big Deal', StageName = 'Negotiation', CloseDate = Date.today().addDays(30), AccountId = account.Id);
insert opportunity;
DailyOpportunityFollowUp job = new DailyOpportunityFollowUp();
job.execute(null);
List<Task> tasks = [SELECT Id FROM Task WHERE WhatId = :opportunity.Id];
delete tasks;
delete opportunity;
delete account;
Assert.areEqual(1, tasks.size(), 'One task should be created for the Negotiation opportunity');

Account account = new Account(Name = 'Test Account For FollowUp');
insert account;
Opportunity opportunity = new Opportunity(Name = 'Big Deal', StageName = 'Negotiation', CloseDate = Date.today().addDays(30), AccountId = account.Id);
insert opportunity;
DailyOpportunityFollowUp job = new DailyOpportunityFollowUp();
job.execute(null);
Task createdTask = [SELECT Subject FROM Task WHERE WhatId = :opportunity.Id LIMIT 1];
delete createdTask;
delete opportunity;
delete account;
Assert.areEqual('Follow up: Big Deal', createdTask.Subject, 'Task subject should include opportunity name');

Account account = new Account(Name = 'Test Account For FollowUp');
insert account;
Opportunity opportunity = new Opportunity(Name = 'Big Deal', StageName = 'Negotiation', CloseDate = Date.today().addDays(30), AccountId = account.Id);
insert opportunity;
DailyOpportunityFollowUp job = new DailyOpportunityFollowUp();
job.execute(null);
Task createdTask = [SELECT WhatId FROM Task WHERE WhatId = :opportunity.Id LIMIT 1];
delete createdTask;
delete opportunity;
delete account;
Assert.areEqual(opportunity.Id, createdTask.WhatId, 'Task should be linked to the opportunity');

Account account = new Account(Name = 'Test Account For FollowUp');
insert account;
Opportunity opportunity = new Opportunity(Name = 'Big Deal', StageName = 'Negotiation', CloseDate = Date.today().addDays(30), AccountId = account.Id);
insert opportunity;
DailyOpportunityFollowUp job = new DailyOpportunityFollowUp();
job.execute(null);
Task createdTask = [SELECT ActivityDate FROM Task WHERE WhatId = :opportunity.Id LIMIT 1];
delete createdTask;
delete opportunity;
delete account;
Assert.areEqual(Date.today().addDays(1), createdTask.ActivityDate, 'Task ActivityDate should be tomorrow');

