public class DailyLeadRatingUpdater implements Schedulable {
    public void execute(SchedulableContext context) {
        List<Lead> leads = [SELECT Id, NumberOfEmployees, Rating FROM Lead WHERE NumberOfEmployees != null];
        for (Lead lead : leads) {
            if (lead.NumberOfEmployees > 1000) {
                lead.Rating = 'Hot';
            } else if (lead.NumberOfEmployees >= 100) {
                lead.Rating = 'Warm';
            }
        }
        update leads;
    }
}

tests:
DailyLeadRatingUpdater job = new DailyLeadRatingUpdater();
job.execute(null);
Assert.isNotNull(job, 'DailyLeadRatingUpdater class must exist with execute method');

Lead bigLead = new Lead(LastName = 'Big Company', Company = 'Big Corp', NumberOfEmployees = 5000);
insert bigLead;
DailyLeadRatingUpdater job = new DailyLeadRatingUpdater();
job.execute(null);
Lead result = [SELECT Rating FROM Lead WHERE Id = :bigLead.Id];
delete bigLead;
Assert.areEqual('Hot', result.Rating, 'Leads with more than 1000 employees should be Hot');

Lead mediumLead = new Lead(LastName = 'Medium Company', Company = 'Medium Corp', NumberOfEmployees = 500);
insert mediumLead;
DailyLeadRatingUpdater job = new DailyLeadRatingUpdater();
job.execute(null);
Lead result = [SELECT Rating FROM Lead WHERE Id = :mediumLead.Id];
delete mediumLead;
Assert.areEqual('Warm', result.Rating, 'Leads with 100-1000 employees should be Warm');

Lead smallLead = new Lead(LastName = 'Small Company', Company = 'Small Corp', NumberOfEmployees = 50);
insert smallLead;
DailyLeadRatingUpdater job = new DailyLeadRatingUpdater();
job.execute(null);
Lead result = [SELECT Rating FROM Lead WHERE Id = :smallLead.Id];
delete smallLead;
Assert.isNull(result.Rating, 'Leads with less than 100 employees should not have Rating changed');

Lead edgeLead = new Lead(LastName = 'Edge Company', Company = 'Edge Corp', NumberOfEmployees = 100);
insert edgeLead;
DailyLeadRatingUpdater job = new DailyLeadRatingUpdater();
job.execute(null);
Lead result = [SELECT Rating FROM Lead WHERE Id = :edgeLead.Id];
delete edgeLead;
Assert.areEqual('Warm', result.Rating, 'Leads with exactly 100 employees should be Warm');

