public class DailyCaseAutoClose implements Schedulable {
    public void execute(SchedulableContext context) {
        List<Case> cases = [SELECT Id, Status FROM Case WHERE Status = 'Waiting on Customer' AND Priority = 'Low'];
        for (Case caseRecord : cases) {
            caseRecord.Status = 'Closed';
        }
        update cases;
    }
}

tests:
DailyCaseAutoClose job = new DailyCaseAutoClose();
job.execute(null);
Assert.isNotNull(job, 'DailyCaseAutoClose class must exist with execute method');

Case lowPriorityCase = new Case(Subject = 'Low Priority Waiting Case', Status = 'Waiting on Customer', Priority = 'Low');
insert lowPriorityCase;
DailyCaseAutoClose job = new DailyCaseAutoClose();
job.execute(null);
Case result = [SELECT Status FROM Case WHERE Id = :lowPriorityCase.Id];
delete result;
Assert.areEqual('Closed', result.Status, 'Low priority cases waiting on customer should be closed');

Case highPriorityCase = new Case(Subject = 'High Priority Waiting Case', Status = 'Waiting on Customer', Priority = 'High');
insert highPriorityCase;
DailyCaseAutoClose job = new DailyCaseAutoClose();
job.execute(null);
Case result = [SELECT Status FROM Case WHERE Id = :highPriorityCase.Id];
delete result;
Assert.areEqual('Waiting on Customer', result.Status, 'High priority cases should remain open');

Case newStatusCase = new Case(Subject = 'New Status Case', Status = 'New', Priority = 'Low');
insert newStatusCase;
DailyCaseAutoClose job = new DailyCaseAutoClose();
job.execute(null);
Case result = [SELECT Status FROM Case WHERE Id = :newStatusCase.Id];
delete result;
Assert.areEqual('New', result.Status, 'Cases with different status should not be closed');

