public class OpportunityTaskQueueable implements Queueable {
    private Set<Id> opportunityIds;

    public OpportunityTaskQueueable(Set<Id> opportunityIds) {
        this.opportunityIds = opportunityIds;
    }

    public void execute(QueueableContext context) {
        List<Opportunity> opps = [SELECT Id, Name, CloseDate FROM Opportunity WHERE Id IN :opportunityIds];
        List<Task> tasks = new List<Task>();
        for (Opportunity opp : opps) {
            if (opp.CloseDate >= Date.today() && opp.CloseDate <= Date.today().addDays(7)) {
                Task t = new Task();
                t.Subject = 'Urgent: Close date approaching for ' + opp.Name;
                t.WhatId = opp.Id;
                t.ActivityDate = Date.today();
                tasks.add(t);
            }
        }
        insert tasks;
    }
}

