public class DuplicateContactQueueable implements Queueable {
    private Set<Id> accountIds;

    public DuplicateContactQueueable(Set<Id> accountIds) {
        this.accountIds = accountIds;
    }

    public void execute(QueueableContext context) {
        List<Contact> contacts = [SELECT Id, Email, Description FROM Contact WHERE AccountId IN :accountIds AND Email != null];
        Map<String, List<Contact>> emailToContacts = new Map<String, List<Contact>>();
        for (Contact contact : contacts) {
            if (!emailToContacts.containsKey(contact.Email)) {
                emailToContacts.put(contact.Email, new List<Contact>());
            }
            emailToContacts.get(contact.Email).add(contact);
        }
        List<Contact> contactsToUpdate = new List<Contact>();
        for (String email : emailToContacts.keySet()) {
            List<Contact> contactList = emailToContacts.get(email);
            if (contactList.size() > 1) {
                for (Contact contact : contactList) {
                    contact.Description = 'Possible Duplicate';
                    contactsToUpdate.add(contact);
                }
            }
        }
        update contactsToUpdate;
    }
}

