public class AccountReassignmentQueueable implements Queueable {
    private Set<Id> accountIds;
    private Id newOwnerId;

    public AccountReassignmentQueueable(Set<Id> accountIds, Id newOwnerId) {
        this.accountIds = accountIds;
        this.newOwnerId = newOwnerId;
    }

    public void execute(QueueableContext context) {
        List<Account> accounts = [SELECT Id, OwnerId, Description FROM Account WHERE Id IN :accountIds];
        List<Account> accountsToUpdate = new List<Account>();
        for (Account account : accounts) {
            if (account.Description == 'Needs Reassignment') {
                account.OwnerId = newOwnerId;
                account.Description = 'Owner reassigned';
                accountsToUpdate.add(account);
            }
        }
        update accountsToUpdate;
    }
}

