public class OverlappingOpportunityQueueable implements Queueable {
    private Set<Id> accountIds;

    public OverlappingOpportunityQueueable(Set<Id> accountIds) {
        this.accountIds = accountIds;
    }

    public void execute(QueueableContext context) {
        List<Opportunity> opportunities = [SELECT Id, AccountId, CloseDate, Description FROM Opportunity WHERE AccountId IN :accountIds];
        Map<String, List<Opportunity>> keyToOpportunities = new Map<String, List<Opportunity>>();
        for (Opportunity opportunity : opportunities) {
            String key = opportunity.AccountId + '_' + String.valueOf(opportunity.CloseDate);
            if (!keyToOpportunities.containsKey(key)) {
                keyToOpportunities.put(key, new List<Opportunity>());
            }
            keyToOpportunities.get(key).add(opportunity);
        }
        List<Opportunity> opportunitiesToUpdate = new List<Opportunity>();
        for (String key : keyToOpportunities.keySet()) {
            List<Opportunity> opportunityList = keyToOpportunities.get(key);
            if (opportunityList.size() > 1) {
                for (Opportunity opportunity : opportunityList) {
                    opportunity.Description = 'Overlapping Close Date';
                    opportunitiesToUpdate.add(opportunity);
                }
            }
        }
        update opportunitiesToUpdate;
    }
}

