public class LeadScoringSummaryBatch implements Database.Batchable<sObject> {
    public Database.QueryLocator start(Database.BatchableContext context) {
        return Database.getQueryLocator([SELECT Id, Name, NumberOfEmployees, AnnualRevenue, Industry, Description FROM Lead WHERE IsConverted = false]);
    }

    public void execute(Database.BatchableContext context, List<Lead> scope) {
        processLeads(scope);
        update scope;
    }

    private void processLeads(List<Lead> leads) {
        for (Lead lead : leads) {
            lead.Description = buildScoreSummary(lead);
        }
    }

    private Integer calculateScore(Lead lead) {
        Integer score = 0;
        if (lead.NumberOfEmployees > 500) {
            score += 30;
        } else if (lead.NumberOfEmployees > 100) {
            score += 20;
        } else {
            score += 10;
        }
        if (lead.AnnualRevenue > 1000000) {
            score += 25;
        } else if (lead.AnnualRevenue > 100000) {
            score += 15;
        } else {
            score += 5;
        }
        return score;
    }

    private String buildScoreSummary(Lead lead) {
        return 'Score: ' + calculateScore(lead) + ' | Industry: ' + lead.Industry + ' | Employees: ' + lead.NumberOfEmployees;
    }

    public void finish(Database.BatchableContext context) {
    }
}

