Assert.areEqual('INV-2024-001', result, 'Should return INV-2024-001');

InvoiceProcessor processor = new InvoiceProcessor();
String json = '{"invoiceId": "INV-2024-001", "total": 1250.00, "paid": false, "lineItems": ["Service A", "Service B", "Service C"]}';
