EmployeeParser parser = new EmployeeParser();
String json = '{"employeeId": "EMP-001", "salary": 75000.00, "active": true, "department": null}';
Boolean result = parser.isActive(json);
Assert.isTrue(result, 'Should return true');

EmployeeParser parser = new EmployeeParser();
String json = '{"employeeId": "EMP-001", "salary": 75000.00, "active": true, "department": null}';
Boolean result = parser.hasDepartment(json);
Assert.isFalse(result, 'Should return false for null department');

EmployeeParser parser = new EmployeeParser();
String json = '{"employeeId": "EMP-002", "salary": 85000.00, "active": false, "department": "Engineering"}';
