public class AccountBuilder {
    public Account buildAccount(String jsonString) {
        Map<String, Object> data = (Map<String, Object>) JSON.deserializeUntyped(jsonString);
        Account acc = new Account();
        acc.Name = (String) data.get('companyName');
        acc.Website = (String) data.get('website');
        acc.AnnualRevenue = (Decimal) data.get('revenue');
        acc.NumberOfEmployees = (Integer) data.get('employees');
        return acc;
    }
}

