public class TicketParser {
    public class Customer {
        public String customerId;
        public String name;
    }

    public class Agent {
        public String agentId;
        public String name;
    }

    public class Ticket {
        public String ticketId;
        public String subject;
        public Customer customer;
        public Agent assignee;
    }

    public String getCustomerName(String jsonString) {
        Ticket ticket = (Ticket) JSON.deserialize(jsonString, Ticket.class);
        return ticket.customer.name;
    }

    public String getAssigneeName(String jsonString) {
        Ticket ticket = (Ticket) JSON.deserialize(jsonString, Ticket.class);
        return ticket.assignee.name;
    }
}

