public class PaymentLinkCallout {
    public static final String MOCK_ENDPOINT = 'https://735ef563-176d-431c-85ce-2cc07056ef03.mock.pstmn.io/payments/link';

    public String generateLink(Contact contact) {
        Logger.info('Generating payment link for Contact', contact);

        HttpRequest request = new HttpRequest();
        request.setMethod('GET');
        request.setEndpoint(MOCK_ENDPOINT);

        Http http = new Http();
        HttpResponse response;

        try {
            response = http.send(request);
        } catch (Exception e) {
            Logger.error('HTTP callout failed', e);
            Logger.saveLog();
            return null;
        }

        Map<String, Object> parsedBody = (Map<String, Object>) JSON.deserializeUntyped(response.getBody());
        Map<String, Object> parsedPayer = (Map<String, Object>) parsedBody.get('payer');
        String paymentLink = (String) parsedPayer.get('link');

        Logger.info('Payment link generated successfully');
        Logger.saveLog();
        return paymentLink;
    }
}

