public class StripeCustomerService {
    private String apiKey = 'sk_test_your_key_here';

    public HttpRequest buildCreateCustomerRequest(Contact contact) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/customers');
        request.setMethod('POST');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        request.setHeader('Content-Type', 'application/x-www-form-urlencoded');
        String body = 'email=' + EncodingUtil.urlEncode(contact.Email, 'UTF-8') + '&name=' + EncodingUtil.urlEncode(contact.FirstName + ' ' + contact.LastName, 'UTF-8');
        request.setBody(body);
        return request;
    }

    public String createCustomer(Contact contact) {
        HttpRequest request = buildCreateCustomerRequest(contact);
        Http http = new Http();
        HttpResponse response = http.send(request);
        return response.getBody();
    }

    public List<Contact> getCustomersByEmail(String email) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/customers/search?query=email:\'' + EncodingUtil.urlEncode(email, 'UTF-8') + '\'');
        request.setMethod('GET');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        Http http = new Http();
        HttpResponse response = http.send(request);
        List<Contact> contacts = new List<Contact>();
        Map<String, Object> result = (Map<String, Object>) JSON.deserializeUntyped(response.getBody());
        List<Object> data = (List<Object>) result.get('data');
        for (Object item : data) {
            Map<String, Object> customer = (Map<String, Object>) item;
            Contact c = new Contact();
            c.Email = (String) customer.get('email');
            c.LastName = (String) customer.get('name');
            contacts.add(c);
        }
        return contacts;
    }
}

