public class StripeProductService {
    private String apiKey = 'sk_test_your_key_here';

    public HttpRequest buildCreateProductRequest(Product2 product) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/products');
        request.setMethod('POST');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        request.setHeader('Content-Type', 'application/x-www-form-urlencoded');
        String body = 'name=' + EncodingUtil.urlEncode(product.Name, 'UTF-8') + '&description=' + EncodingUtil.urlEncode(product.Description, 'UTF-8');
        request.setBody(body);
        return request;
    }

    public String createProduct(Product2 product) {
        HttpRequest request = buildCreateProductRequest(product);
        Http http = new Http();
        HttpResponse response = http.send(request);
        return response.getBody();
    }

    public List<Product2> getProductsByName(String name) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/products/search?query=name~\'' + EncodingUtil.urlEncode(name, 'UTF-8') + '\'');
        request.setMethod('GET');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        Http http = new Http();
        HttpResponse response = http.send(request);
        List<Product2> products = new List<Product2>();
        Map<String, Object> result = (Map<String, Object>) JSON.deserializeUntyped(response.getBody());
        List<Object> data = (List<Object>) result.get('data');
        for (Object item : data) {
            Map<String, Object> prod = (Map<String, Object>) item;
            Product2 p = new Product2();
            p.Name = (String) prod.get('name');
            p.Description = (String) prod.get('description');
            products.add(p);
        }
        return products;
    }
}

