public class StripeInvoiceService {
    private String apiKey = 'sk_test_your_key_here';

    public HttpRequest buildCreateInvoiceRequest(String customerId, String description) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/invoices');
        request.setMethod('POST');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        request.setHeader('Content-Type', 'application/x-www-form-urlencoded');
        String body = 'customer=' + EncodingUtil.urlEncode(customerId, 'UTF-8') + '&description=' + EncodingUtil.urlEncode(description, 'UTF-8');
        request.setBody(body);
        return request;
    }

    public String createInvoice(String customerId, String description) {
        HttpRequest request = buildCreateInvoiceRequest(customerId, description);
        Http http = new Http();
        HttpResponse response = http.send(request);
        return response.getBody();
    }

    public List<Map<String, Object>> getInvoicesByCustomer(String customerId) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/invoices?customer=' + EncodingUtil.urlEncode(customerId, 'UTF-8'));
        request.setMethod('GET');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        Http http = new Http();
        HttpResponse response = http.send(request);
        List<Map<String, Object>> invoices = new List<Map<String, Object>>();
        Map<String, Object> result = (Map<String, Object>) JSON.deserializeUntyped(response.getBody());
        List<Object> data = (List<Object>) result.get('data');
        for (Object item : data) {
            invoices.add((Map<String, Object>) item);
        }
        return invoices;
    }
}

