public class StripeCouponService {
    private String apiKey = 'sk_test_your_key_here';

    public HttpRequest buildCreateCouponRequest(String couponId, Integer percentOff) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/coupons');
        request.setMethod('POST');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        request.setHeader('Content-Type', 'application/x-www-form-urlencoded');
        String body = 'id=' + EncodingUtil.urlEncode(couponId, 'UTF-8') + '&percent_off=' + percentOff;
        request.setBody(body);
        return request;
    }

    public String createCoupon(String couponId, Integer percentOff) {
        HttpRequest request = buildCreateCouponRequest(couponId, percentOff);
        Http http = new Http();
        HttpResponse response = http.send(request);
        return response.getBody();
    }

    public Map<String, Object> getCouponById(String couponId) {
        HttpRequest request = new HttpRequest();
        request.setEndpoint('https://api.stripe.com/v1/coupons/' + EncodingUtil.urlEncode(couponId, 'UTF-8'));
        request.setMethod('GET');
        request.setHeader('Authorization', 'Bearer ' + apiKey);
        Http http = new Http();
        HttpResponse response = http.send(request);
        return (Map<String, Object>) JSON.deserializeUntyped(response.getBody());
    }
}

