BankAccount testAccount = new BankAccount(); 
testAccount.accountNumber = 'CHK-001'; 
testAccount.accountType = 'Checking'; 
testAccount.balance = 0; 

testAccount.deposit(1000); 
Assert.areEqual(1000, testAccount.balance, 'Deposit should add to the balance');

BankAccount testAccount = new BankAccount(); 
testAccount.accountNumber = 'CHK-001'; 
testAccount.accountType = 'Checking'; 
testAccount.balance = 0; 

testAccount.deposit(0); 
Assert.areEqual(0, testAccount.balance, 'Deposit should not add anything to the balance if we are adding 0');

 BankAccount testAccount = new BankAccount(); 
testAccount.accountNumber = 'CHK-001'; 
testAccount.accountType = 'Checking'; 
testAccount.balance = 1000; 

testAccount.withdraw(250); 
Assert.areEqual(750, testAccount.balance, 'Withdraw should subtract from the balance');

 BankAccount testAccount = new BankAccount(); 
testAccount.accountNumber = 'CHK-001'; 
testAccount.accountType = 'Checking'; 
testAccount.balance = 750; 

String accountDetails = testAccount.getAccountDetails(); 

Assert.isTrue(accountDetails.contains('CHK-001'), 'Account details should contain the account number'); 

Assert.isTrue(accountDetails.contains('750'), 'Account details should contain the current balance');

BankTransaction testTransaction = new BankTransaction();
testTransaction.transactionId = 'TXN-001';
testTransaction.amount = 1000;
testTransaction.transactionType = 'Deposit';

Decimal fee = testTransaction.calculateTransactionFee();
Assert.areEqual(2.5, fee, 'Transaction fee should be 0.25% of transaction amount');

BankTransaction testTransaction = new BankTransaction();
testTransaction.transactionId = 'TXN-001';
testTransaction.amount = 1000;
testTransaction.transactionType = 'Withdrawal';
testTransaction.transactionDate = Date.today();

String details = testTransaction.getTransactionDetails();
Assert.isTrue(details.contains('TXN-001'), 'Transaction details should contain the transaction ID');
Assert.isTrue(details.contains('1000'), 'Transaction details should contain the transaction amount');

 Customer testCustomer = new Customer(); 
testCustomer.customerId = 'CUST-001'; 
testCustomer.name = 'John Doe'; 

BankAccount account1 = new BankAccount(); 
account1.accountNumber = 'CHK-001'; 
account1.balance = 1000; 

BankAccount account2 = new BankAccount(); 
account2.accountNumber = 'SAV-001'; 
account2.balance = 2500; 

testCustomer.setPrimaryAccount(account1); 
testCustomer.setSecondaryAccount(account2); 

Decimal totalBalance = testCustomer.calculateTotalBalance(); 
Assert.areEqual(3500, totalBalance, 'Total balance should be the sum of all account balances');

 Customer testCustomer = new Customer(); 
testCustomer.customerId = 'CUST-001'; 
testCustomer.name = 'John Doe'; 
testCustomer.contactInfo = 'johndoe@email.com'; 

BankAccount account1 = new BankAccount(); 
account1.accountNumber = 'CHK-001'; 
account1.balance = 1000; 

BankAccount account2 = new BankAccount(); 
account2.accountNumber = 'SAV-001'; 
account2.balance = 2500; 

testCustomer.setPrimaryAccount(account1); 
testCustomer.setSecondaryAccount(account2); 

String summary = testCustomer.getCustomerSummary(); 
Assert.isTrue(summary.contains('John Doe'), 'Customer summary should contain the customer name'); 
Assert.isTrue(summary.contains('3500'), 'Customer summary should contain the total balance');

 InterestCalculator interestCalculator = new InterestCalculator();

BankAccount savingsAccount = new BankAccount();
savingsAccount.accountNumber = 'SAV-001';
savingsAccount.accountType = 'Savings';
savingsAccount.balance = 1000;

Decimal annualRate = 0.025;
Decimal months = 12;
Decimal interest = interestCalculator.calculateSavingsInterest(savingsAccount, annualRate, months);
Assert.areEqual(25.0, interest, 'Savings interest should be calculated at 2.5% annual rate');

 InterestCalculator interestCalculator = new InterestCalculator();

BankAccount checkingAccount = new BankAccount();
checkingAccount.accountNumber = 'CHK-001';
checkingAccount.accountType = 'Checking';
checkingAccount.balance = 1000;

Decimal annualRate = 0.005;
Decimal months = 12;
Decimal interest = interestCalculator.calculateCheckingInterest(checkingAccount, annualRate, months);
