 InterestCalculator interestCalculator = new InterestCalculator();

BankAccount savingsAccount = new BankAccount();
savingsAccount.accountNumber = 'SAV-001';
savingsAccount.accountType = 'Savings';
savingsAccount.balance = 1000;

BankAccount checkingAccount = new BankAccount();
checkingAccount.accountNumber = 'CHK-001';
checkingAccount.accountType = 'Checking';
checkingAccount.balance = 2000;

Decimal savingsRate = 0.025;
Decimal checkingRate = 0.005; 
Decimal months = 12;

Decimal totalInterest = interestCalculator.calculateTotalInterestEarned(
    savingsAccount, checkingAccount, savingsRate, checkingRate, months);
Assert.areEqual(35.0, totalInterest, 'Total interest should be the sum of interest from both accounts');
