// HTML
<template>
    <lightning-input label="Enter your name" value={name}
        onchange={handleNameChange}></lightning-input>
    <p>{greeting}</p>
</template>

// JS
import { LightningElement } from 'lwc';
export default class GreetingInput extends LightningElement {
    name = '';

    handleNameChange(event) {
        this.name = event.target.value;
    }

    get greeting() {
        if (this.name) {
            return 'Hello, ' + this.name + '!';
        }
        return 'Hello, stranger!';
    }
}

