// HTML
<template>
    <button onclick={toggleVisibility}>Toggle Message</button>
    <p lwc:if={isVisible}>Now you see me!</p>
</template>

// JS
import { LightningElement } from 'lwc';
export default class ToggleMessage extends LightningElement {
    isVisible = false;

    toggleVisibility() {
        this.isVisible = !this.isVisible;
    }
}

