// HTML
<template>
    <h3>Salesforce Clouds</h3>
    <ul>
        <template for:each={clouds} for:item="cloud">
            <li key={cloud.id}>{cloud.name}</li>
        </template>
    </ul>
</template>

// JS
import { LightningElement } from 'lwc';
export default class CloudList extends LightningElement {
    clouds = [
        { id: 1, name: 'Sales Cloud' },
        { id: 2, name: 'Service Cloud' },
        { id: 3, name: 'Marketing Cloud' }
    ];
}

