function myReduce(array, callbackFunction, initialValue){

    let accumulator = initialValue;
    
  
    for(let i = 0; i < array.length; i++){
        let element = array[i];
        accumulator = callbackFunction(accumulator, element);
    }
    return accumulator;
}
