export default class Week9Child extends LightningElement {
    @api accountId;
    firstName = '';
    lastName = '';
    phone = '';
    email = '';
    savingContacts = false;

    handleFirstNameChange(event) {
        this.firstName = event.target.value;
    }
    handleLastNameChange(event) {
        this.lastName = event.target.value;
    }
    handlePhoneChange(event) {
        this.phone = event.target.value;
    }
    handleEmailChange(event) {
        this.email = event.target.value;
    }
    

    handleNewContact(event){
        this.savingContacts = true;
        saveContact({
            firstName: this.firstName,
            lastName: this.lastName,
            email: this.email,
            phone: this.phone,
            accountId: this.accountId
            
        }).then(() => {
            this.dispatchEvent(
                new CustomEvent('newcontact', {
                    detail: {
                        FirstName: this.firstName,
                        LastName: this.lastName,
                        Email: this.email,
                        Phone: this.phone
                    }
                }) 
            );

            this.clearFields();
            this.savingContacts=false;
        }).catch(error => {
            console.error('Error saving contact: ', error);
            this.savingContacts=false;
        });   
    }

    clearFields() {
        this.firstName = '';
        this.lastName = '';
        this.phone = '';
        this.email = '';;
    }
    
}

public with sharing class caseCommentController {
    @AuraEnabled
    public static List<Case_Comment__c> getCaseComments(Id caseId){
        return [
            SELECT Id, Name, Comment__c, Category__c, Visibility__c, CreatedDate, CreatedById, CreatedBy.Name, Case__c
            FROM Case_Comment__c
            WHERE Case__c = :caseId
            ORDER BY CreatedDate DESC
        ];
    }

    @AuraEnabled
    public static Case_Comment__c saveCaseComment(Id caseId, String comment, String category, String visibility){
        Case_Comment__c caseComment = new Case_Comment__c(
            Case__c = caseId,
            Comment__c = comment,
            Category__c = category,
            Visibility__c = visibility
        );

        insert caseComment;

        return [
            SELECT Id, Name, Comment__c, Category__c, Visibility__c, CreatedDate, CreatedById, CreatedBy.Name, Case__c
            FROM Case_Comment__c
            WHERE Id = :caseComment.Id
            LIMIT 1
        ];

    }
}

