        setTimeout(() => {
            getCaseComments({caseId: this.recordId})
            .then(result => {
                this.caseComments = result.map(row => ({
                    ...row,
                    createdByName: row.CreatedBy.Name
                }));
            })
            .catch(error => {
                console.log(error);
            });
        },500);
            
    }
}

