let cleanedData = function () {
  let data = rawData
    .replace(/[-_]+/g, ' ')      
    .replace(/\s+/g, ' ')        
    .trim()
    .toLowerCase();

  let words = data.split(',');
  
  let result = [];

  for (let i = 0; i < words.length; i++) {
    let [user, domain] = words[i].trim().split('@');
    let userWords = user.trim().split(' ');

    let capitalizedUser = '';
    for (let j = 0; j < userWords.length; j++) {
      let word = userWords[j];
      if (word) {
        capitalizedUser += word.charAt(0).toUpperCase() + word.slice(1);
        if (j !== userWords.length - 1) {
          capitalizedUser += ' ';
        }
      }
    }

    let secondWord = userWords[1] ? userWords[1] : '';
    result.push(`${capitalizedUser} <${secondWord}@${domain.trim()}>`);
  }

  return result; 
};


