function formatContacts(data) {
  let items = data.split(',');  
  let contacts = [];

  for (let i = 0; i < items.length; i++) {
    let line = items[i].trim();  
    if (line === '') continue;  
   
    let atIndex = line.indexOf('@');
    if (atIndex === -1) continue;  

    let namePart = line.slice(0, atIndex).trim();
    let domainPart = line.slice(atIndex + 1).trim();

     
    let cleanName = '';
    for (let j = 0; j < namePart.length; j++) {
      let char = namePart[j];
      if (char === '_' || char === '-') {
        cleanName += ' ';
      } else {
        cleanName += char;
      }
    }
  
    let words = cleanName.split(' ');
    let capitalizedWords = [];
    for (let j = 0; j < words.length; j++) {
      let word = words[j].trim();
      if (word.length > 0) {
        let capWord = word[0].toUpperCase() + word.slice(1).toLowerCase();
        capitalizedWords.push(capWord);
      }
    }

    let fullName = capitalizedWords.join(' ');
    let lastName = capitalizedWords[capitalizedWords.length - 1] || '';
    let email = lastName.toLowerCase() + '@' + domainPart.toLowerCase();

    contacts.push(fullName + ' <' + email + '>');
  }
 
  for (let i = 0; i < contacts.length - 1; i++) {
    for (let j = i + 1; j < contacts.length; j++) {
      if (contacts[i] > contacts[j]) {
        let temp = contacts[i];
        contacts[i] = contacts[j];
        contacts[j] = temp;
      }
    }
  }

  return contacts;
}

console.log(formatContacts(rawData));

// Sample contact CSV data (as a string)
const csvData = `
First Name,Last Name,Email,Company,Title
Anna,Smith,anna@example.com,Acme Inc.,Manager
Brian,,brian@email.com,,Developer
Claire,Brown,,Acme Inc.,Intern
John,Doe,john.doe@example.com,Salesforce,Admin
Ella,Fitzgerald,ella@jazzmail.com,Jazz Corp,VP
Tom,,tom123@invalid,N/A,Consultant
Maria,Garcia,maria.garcia@company.com,Acme Inc.,Sales
Derek,Lee,,Salesforce,Engineer
Sophie,Khan,sophie.khan@email.com,StartupX,CEO
Kevin,Nguyen,,StartupX,Intern
`.trim();

function parseAndClean(csv) {
  const lines = csv.split("\n");
  const headers = lines[0].split(",");
  
  const validContacts = [];
  const invalidContacts = [];
  let unknownTitleCount = 0;

  for (let i = 1; i < lines.length; i++) {
    const row = lines[i].split(",");

    let firstName = row[0]?.trim() || "Unknown";
    let lastName = row[1]?.trim() || "Unknown";
    let email = row[2]?.trim() || "no-email@example.com";
    let company = row[3]?.trim() || "N/A";
    let title = row[4]?.trim() || "Unknown";

    if (title === "Unknown") unknownTitleCount++;

    const originalFirstName = row[0]?.trim();
    const originalEmail = row[2]?.trim();

    
    const isEmailValid = originalEmail?.includes("@") && originalEmail.indexOf(".") > originalEmail.indexOf("@");
    const isValid = originalFirstName && isEmailValid;

    const contact = { firstName, lastName, email, company, title };

    if (isValid) {
      validContacts.push(contact);
    } else {
      contact.invalid = true;
      invalidContacts.push(contact);
    }
  }

  return { validContacts, invalidContacts, unknownTitleCount };
}



function normalizeTitles(contacts) {
  const keyContacts = [];

  for (let contact of contacts) {
    let title = contact.title.toLowerCase();

    if (title === "ceo" || title === "chief executive officer") {
      contact.title = "CEO";
    } else if (title === "vp") {
      contact.title = "VP";
    } else if (title === "manager") {
      contact.title = "Manager";
    }

    if (["CEO", "VP", "Manager"].includes(contact.title)) {
      keyContacts.push(contact);
    }
  }

  return keyContacts;
}



function groupByCompany(contacts) {
  const grouped = {};
  const summary = {};

  for (let contact of contacts) {
    const company = contact.company;

    if (!grouped[company]) {
      grouped[company] = [];
    }
    grouped[company].push(contact);
  }

  for (let company in grouped) {
    summary[company] = grouped[company].length;
  }

  return { grouped, summary };
}



function wait(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}



async function runImport() {
  console.log("Starting Contact Import...\n");

  const { validContacts, invalidContacts, unknownTitleCount } = parseAndClean(csvData);
  await wait(1000);
  console.log(" Step 1: Cleaned and validated data.");
  console.log(` Valid: ${validContacts.length}`);
  console.log(` Invalid: ${invalidContacts.length}\n`);

  const keyContacts = normalizeTitles(validContacts);
