public with sharing class dashboardController {
   @AuraEnabled(cacheable=true)
   public static Map<String, Object> getDashboardMetrics() {
       Map<String, Object> data = new Map<String, Object>();

       Date startOfMonth = Date.today().toStartOfMonth();
       Date endOfMonth = startOfMonth.addMonths(1).addDays(-1);
       Date startOfQ2 = Date.newInstance(Date.today().year(), 4, 1);
       Date endOfQ2 = Date.newInstance(Date.today().year(), 6, 30);

       // New Leads (This Month)
       Integer newLeads = [SELECT COUNT() FROM Lead WHERE CreatedDate = THIS_MONTH];
       
       // Closed Deals (Q2)
       Integer closedDeals = [
           SELECT COUNT() FROM Opportunity 
           WHERE StageName = 'Closed Won' 
           AND CloseDate >= :startOfQ2 AND CloseDate <= :endOfQ2
       ];

       // Pipeline Value (Current Quarter)
       AggregateResult[] result = [
         SELECT SUM(Amount) totalAmount FROM Opportunity 
         WHERE IsClosed = FALSE 
         AND CloseDate = THIS_QUARTER
     ];
     
     Decimal pipelineValue = (result.size() > 0 && result[0].get('totalAmount') != null) 
         ? (Decimal) result[0].get('totalAmount') 
         : 0;
     
     data.put('pipelineValue', pipelineValue);
     

       // Open Cases
       Integer openCases = [SELECT COUNT() FROM Case WHERE Status != 'Closed'];

       data.put('newLeads', newLeads);
       data.put('closedDeals', closedDeals);
       data.put('pipelineValue', pipelineValue != null ? pipelineValue : 0);
       data.put('openCases', openCases);

       return data;
   }
}
