import { LightningElement } from 'lwc';

export default class Task5MathCalculations extends LightningElement {
    firstNumber;
    secondNumber;
    handleFirstNumberChange(event){
        this.firstNumber=parseFloat(event.target.value);
    }
    handleSecondNumberChange(event){
        this.secondNumber=parseFloat(event.target.value);
    }
    get hasValidInputs(){
        return !isNaN(this.firstNumber) && !isNaN(this.secondNumber);
    }
    get sum(){
        return this.firstNumber+this.secondNumber;
    }
    get difference(){
        return this.firstNumber-this.secondNumber;
    }
    get product(){
        return this.firstNumber*this.secondNumber;
    }
    get quotient(){
        return this.firstNumber/this.secondNumber;
    }
        
}
