Kevin,Nguyen,,StartupX,Intern
`.trim();

let rows = csvData.split(/\r?\n|\r|\n/g);

let validContacts = [];
let invalidContacts = [];
let unknownTitlesCount = 0;

for (let i = 1; i < rows.length; i++) {
    let rowData = rows[i].split(',');
    let obj = {};

    if (rowData.length === 5) {
        obj.firstName = rowData[0] ? rowData[0] : 'Unknown';
        obj.lastName = rowData[1] ? rowData[1] : 'Unknown';
        obj.email = rowData[2] ? rowData[2] : 'no-email@example.com';
        obj.company = rowData[3] ? rowData[3] : 'N/A';
        obj.title = rowData[4] ? rowData[4] : 'Unknown';
        if (rowData[4] === 'Unknown') {
            unknownTitlesCount += 1;
        }
    }

    // Mark Invalid Contacts
    if (obj.firstName === 'Unknown' || obj.email.indexOf('@') === -1 || obj.email.split('@')[1].indexOf('.') === -1) {
        obj.invalid = true;
        invalidContacts.push(obj);
    } else {
        validContacts.push(obj);
    }
}

let allContacts = [...invalidContacts, ...validContacts];

for (let j = 0; j < allContacts.length; j++) {
    let currentObj = allContacts[j];

    // Normalize title
    if (currentObj.title === 'ceo' || currentObj.title === 'chief executive officer') {
        currentObj.title = 'CEO';
    } else if (currentObj.title === 'vp') {
        currentObj.title = 'VP';
    }
}

let numberOfKeyContacts = 0;
for (let j = 0; j < allContacts.length; j++) {
    if (allContacts[j].title === 'CEO' || allContacts[j].title === 'VP' || allContacts[j].title === 'Manager') {
        numberOfKeyContacts += 1;
    }
}

// Grouping by company
