  "Acme Inc.": [],
  "Salesforce": [],
  "StartupX": [],
  "N/A": []
}

let companyGroupsSummary = {
  "Acme Inc.": 0,
  "Salesforce": 0,
  "StartupX": 0,
  "N/A": 0
}

for (let k = 0; k < validContacts.length; k++) {
    if (validContacts[k].company === 'Acme Inc.') {
        companyGroups['Acme Inc.'].push(validContacts[k]);
        companyGroupsSummary['Acme Inc.'] += 1;
    } else if (validContacts[k].company === 'Salesforce') {
        companyGroups['Salesforce'].push(validContacts[k]);
        companyGroupsSummary['Salesforce'] += 1;
    } else if (validContacts[k].company === 'StartupX') {
        companyGroups['StartupX'].push(validContacts[k]);
        companyGroupsSummary['StartupX'] += 1;
    } else if (validContacts[k].company === 'N/A') {
        companyGroups['N/A'].push(validContacts[k]);
        companyGroupsSummary['N/A'] += 1;
    }
}

// Output
