function cleanData(rawData) {
  // split data and remove new line markers
  let splitData = rawData.replace(/\n/g, "").split(",");
  let formattedData = [];

  // go through each entry and trim and split it into name and email domain
  for (let i = 0; i <= splitData.length - 1; i++) {
    splitData[i] = splitData[i].trim().split("@");

    // extract and capitalize the name
    let fullName = extractName(splitData[i][0]);

    // rebuild email address
    let email = `${fullName[1].toLowerCase()}@${splitData[i][1].toLowerCase()}`;

    // format name and email
    formattedData.push(`${fullName.join(" ")} <${email}>`);
  }

  // return sorted list
  return formattedData.sort();
}

function extractName(name) {
  name = name.trim();

  // replace underscores and dashes
  name = name.replaceAll(/[-_]/g, " ");

  // replace double spaces and split
  name = name.replace(/\s+/g, " ");
  name = name.split(" ");

  // return capitalized first and last
  return [capitalize(name[0]), capitalize(name[1])];
}

