// Okay, this was super cool and I think I only have one question. 
// Was there a way I could have written the hasSameProperties function
// using a "for of" loop like I did with the hasSameValues function?
// I needed the index for the hasSameProperties function so I am not 
// sure if there was a simpler way to do it than using the normal for 
// loop. Thanks.  


isObject = function(value) {
  return typeof value == "object";
}

isEqualLength = function(obj1, obj2) {
  return Object.keys(obj1).length == Object.keys(obj2).length;
}

hasSameProperties = function(obj1, obj2) {
  let props1 = Object.getOwnPropertyNames(obj1).sort();
  let props2 = Object.getOwnPropertyNames(obj2).sort();  
  for (let i = 0; i <= props1.length -1; i++) {
    if (props1[i] !== props2[i])
      return false;
  }
  return true;
}

hasSameValues = function(obj1, obj2) {
  let keys = Object.keys(obj1);
  for (key of keys) {
    if (!deepEqual(obj1[key], obj2[key])) {
      return false;
    }
  }
  return true;
}

function deepEqual(obj1, obj2) {
  // check for primitives and nulls
  if (!isObject(obj1) || !isObject(obj2) || obj1 === null || obj2 === null) {
    return obj1 === obj2;
  }
  // check for equal number of properties
  if (!isEqualLength(obj1, obj2)) {
    return false;    
  }
  // check for the same properties
  if (!hasSameProperties(obj1, obj2)) {
      return false;
  }
  // check for values
  if (!hasSameValues(obj1, obj2)) {
    return false;
  }
  return true;
}

console.log(deepEqual({a: {b: 2}}, {a: {b: 2}}));     // true
console.log(deepEqual({a: {b: 2}}, {a: {b: 3}}));     // false

