const splitLines = (data) => data.split("\n");

const missingNameTitle = (value) => value || "Unknown";

const missingCompany = (value) => value || "N/A";

const missingEmail = (value) => value || "no-email@example.com";

const isValidEmail = (email) => !email || email.includes("@") && email.split("@")[1].includes(".");

const hasStringValue = (string) => typeof string === "string" && string.trim() !== "";

const getISOTime = () => new Date().toISOString();

const getUnknownTitleCount = (contacts) => contacts.filter(contact => contact.title === "Unknown").length;

function parseCSV(myCsvData) {
  let contacts = [];
  let parsedData = splitLines(myCsvData);
  for (let i = 1; i <= parsedData.length - 1; i++) {
    let contactData = parsedData[i].split(",");
    let contact = {
      firstName: contactData[0],
      lastName: contactData[1],
      email: contactData[2],
      company: contactData[3],
      title: contactData[4]
    }
    contacts.push(contact);
  }
  return contacts;
}

function markInvalidContacts(contacts) {
  for (let contact of contacts) {
    if (!isValidEmail(contact.email) || !hasStringValue(contact.firstName)) {
      contact.invalid = true;
    }
  }
  return contacts;
}

function getValidContacts(contacts) {
  let validContacts = [];
  for (let contact of contacts) {
    if (!contact.invalid == true) {
      validContacts.push(contact);
    }
  }
  return validContacts;
}

function getInvalidContacts(contacts) {
  let invalidContacts = [];
  for (let contact of contacts) {
    if (contact.invalid == true) {
      invalidContacts.push(contact);
    }
  }
  return invalidContacts;
}

function cleanContacts(contacts) {
  for (let contact of contacts) {
    contact.lastName = missingNameTitle(contact.lastName);
    contact.title = missingNameTitle(contact.title);
    contact.company = missingCompany(contact.company);
    contact.email = missingEmail(contact.email);
  }
  return contacts
}

function normalizeTitles(contacts) {
  for (let contact of contacts) {
    const title = contact.title.toLowerCase().trim();
    if (["ceo", "chief executive officer"].includes(title)) {
      contact.title = "CEO";
    }
    if (contact.title == "vp") {
      contact.title = "VP";
    }
  }
  return contacts;
}

function getKeyContacts(contacts) {
  let keyContacts = [];
  for (let contact of contacts) {
    if (["CEO", "VP", "Manager"].includes(contact.title)) {
      keyContacts.push(contact)
    }
  }
  return keyContacts;
}

function sortByAccount(contacts) {
  let accounts = {};
  for (let contact of contacts) {
    if (!accounts[contact.company]) {
      accounts[contact.company] = [];
    }
    accounts[contact.company].push(contact);
  }
  return accounts;
}

function generateCompanySummary(accounts) {
  let summary = {};
  for (let company in accounts) {
    summary[company] = accounts[company].length;
  }
  return summary;
}

function finalReport(validContacts, invalidContacts, keyContacts) {
  console.log("üì¶ CRM Import Summary");
  console.log("-------------------------");
  console.log(`üìÖ Timestamp: ${getISOTime()}`);
  if (!(validContacts.length > 0)) {
    console.log("‚ùå Import Failed.");
    console.log("Reason: No valid contacts found.");
    console.log("Please review the data before trying again.");

  } else {
    console.log(`‚úÖ Imported Contacts: ${validContacts.length}`);
    console.log(`‚ùå Skipped Contacts (invalid data): ${invalidContacts.length}`);
    console.log(`üè∑Ô∏è Contacts with Unknown Title: ${getUnknownTitleCount(validContacts)}`);
    console.log(`üß† Key Roles Imported (CEO/VP/Manager): ${keyContacts.length}`);
    console.log("");
    console.log("Import completed successfully.");    
  }  
}

