export default class Week8task5 extends LightningElement {
    inputText = '';
    result = '';

    handleChange(event) {
        this.inputText = event.target.value;
    }

    handleClick(event){
        const entries = this.inputText.trim().split(" ");
        if (!this.validateInput(entries)){
            this.result = "You did not enter two valid numbers with a space in between.";
            return;
        }
        let num1 = Number(entries[0]);
        let num2 = Number(entries[1]);
        let sum = this.add(num1, num2);
        let diff = this.subtract(num1, num2);    
        let product = this.multiply(num1, num2);    
        let quotient = this.divide(num1, num2);        
        this.result = `Sum: ${sum} Difference: ${diff} Product: ${product} Quotient: ${quotient.toFixed(2)}`;
    } 

    validateInput(entries) {
        if (entries.length != 2) return false;
        if (isNaN(entries[0]) || isNaN(entries[1])) return false;
        return true;
    }

    add = (a, b) => a + b;
    subtract = (a, b) => a - b;
    multiply = (a, b) => a * b;
    divide = (a, b) => a / b;
}

