// FInd prime numbers in a range

let startNumber = Number(prompt("Enter start number: "));
let endNumber = Number(prompt("Enter end number: "));
console.log("Primes between ", startNumber, " and ", endNumber);
let passNumber = 0;
//array to store the prime numbers
let primeNumbers = [];

// first loop to go over all the numbers in the range
for (let i = startNumber; i <= endNumber; i++) {
  if(i < 2 ) {
    continue;
  }
  // if number are 1 or 3 they will be pushed to the array 
  if(i === 2 ||i === 3) {
    primeNumbers.push(i);
    console.log(i);
    continue;
  }
  //get the square root of the current number to see if that is divisible between 2 and the square root number
  let sqrRoot = Math.trunc(Math.sqrt(i));
  //current number is the index i which will be pass to the next loop
  passNumber = i;
  // flag to be used in case the number is divisible for any of the number in the loop, if so it wil be set as false, noprime number
  let isPrime = true;
  for (let j = 2; j <= sqrRoot; j++) {
    if(passNumber % j === 0 ) {
      isPrime = false;
      break;
    }
    isPrime = true;
  }
  if (isPrime) {
    primeNumbers.push(passNumber);
    console.log(passNumber);
  }
}
console.log("Prime Numbers ", primeNumbers);

