let deepEqual = function(a, b) {
 if((typeof a) === (typeof b)) {
    if ( JSON.stringify(a) === JSON.stringify(b) ) {
      return true;
    } else {
      return false;
    }
  } else {
    if(a === b) {
      return true;
    } else {
        return false;
    }
  }
}
 console.log("1,1 ", deepEqual(1, 1));                                       // true
console.log(`1, "1" `, deepEqual(1, '1'));                                   // false
console.log("null, null ", deepEqual(null, null));                           // true
console.log("null, {} ", deepEqual(null, {}));                                // false
console.log("{a: 1}, {a: 1} ",deepEqual({a: 1}, {a: 1}));                     // true
console.log("{a: 1}, {a: 2} ",deepEqual({a: 1}, {a: 2}));                     // false
console.log("{a: 1}, {a: 1, b: 2} ",deepEqual({a: 1}, {a: 1, b: 2}));          // false
console.log("{a: {b: 2}}, {a: {b: 2}} ", deepEqual({a: {b: 2}}, {a: {b: 2}}))  //true
