import { LightningElement, api } from 'lwc';
import queryContacts from '@salesforce/apex/ContactDeleteDatatableController.queryContacts';
import saveContacts from '@salesforce/apex/ContactDeleteDatatableController.saveContacts';
import { ShowToastEvent } from 'lightning/platformShowToastEvent';
import deleteContact from '@salesforce/apex/ContactDeleteDatatableController.deleteContact';

export default class ContactDeleteDatatable extends LightningElement {
@api recordId; 
    contacts = [];
    draftValues = [];
    actions= {label: 'Delete', name: 'delete'};

    columns = [
        { label: 'First Name', fieldName: 'FirstName', editable : 'true'},
        { label: 'Last Name', fieldName: 'LastName', editable : 'true'},
        { label: 'Email', fieldName: 'Email', type: 'email', editable : 'true'},
        { label: 'Phone', fieldName: 'Phone', type: 'phone', editable : 'true'},
        { label: 'Delete', 
            type: 'button', 
            typeAttributes: {
                label: 'Delete',
                name: 'delete',
                variant: 'destructive'
            }
        }
    ];

    connectedCallback() {
        queryContacts({accountId: this.recordId})
            .then(result => {
                this.contacts = result;
            })
            .catch(error => {
                console.error('Error fetching contacts:', error);
            });
    }

    handleSave(event) {
        this.draftValues = event.detail.draftValues;
        console.log('this.draftValues ', this.draftValues);
        saveContacts({contacts: this.draftValues, accountId: this.recordId})
            .then(result => {
                this.contacts = result;
                this.draftValues = [];

                this.showToast('Success!', 'Everything saved.', 'success')
            })
            .catch(error => {
                console.error('Error fetching contacts:', error);
            });
    }

    handleCancel(event) {
        this.draftValues = [];
    }

    showToast(title, message, variant) {
        const evt = new ShowToastEvent({
            title: title,
            message: message,
            variant: variant
        });

        this.dispatchEvent(evt);
    }

    handleRowAction(event) {
        const row = event.detail.row;
        console.log('contact to delete ID ', row.Id);
        deleteContact({contactId: row.Id, accountId: this.recordId})
            .then(result => {
                this.contacts = result;

                this.showToast('Success!', 'Contact deleted.', 'success');
            })
            .catch(error => {
                console.error('Error deleting contact:', error);
            });
    }

}

public with sharing class ContactDeleteDatatableController {
     @AuraEnabled(cacheable=true)
    public static List<Contact> queryContacts(Id accountId){
        return [
            SELECT Id, FirstName, LastName, Email, Phone 
            FROM Contact 
            WHERE AccountId = :accountId 
        ];
    }

    @AuraEnabled
    public static List<Contact> saveContacts(List<Contact> contacts, Id accountId){
        update contacts;
        List<Contact> contactsUpdated = [SELECT Id, FirstName, LastName, Email, Phone 
            FROM Contact 
            WHERE AccountId = :accountId ];
        return contactsUpdated;
    }

     @AuraEnabled
    public static List<Contact> deleteContact(Id contactId, Id accountId) {
        delete [SELECT Id from Contact Where Id =: contactId];
        System.debug('deleted contact');
        return [SELECT Id, FirstName, LastName, Email, Phone 
            FROM Contact 
            WHERE AccountId = :accountId ];
    }
}

