function generateSumOfInput(){
  let input = '';
  
  while(input == ''){
    input =  prompt('Enter number:  ');
    let number = Number(input);
    while(Number.isNaN(number) || number <=0 || !Number.isInteger(number) ){
      let input = prompt('No really, please enter a positive whole number:  ');
      number = Number(input);
    } 
    generateTotal(number);
  }

  function generateTotal(number){
    let counter = 0;
    for(let i = 1;i<=number;i++ ){
      counter+=i;
    }
    console.log(counter);
  }
}

generateSumOfInput();


