function numberRepeater(){
  let input = '';
  let number = null;
  while(input == ''){
     input = prompt("Enter number to repeat:  ");
     number = Number(input);
     while(Number.isNaN(number)){
      input = prompt('I would really like a number to repeat. Please enter an actual number: ');
      number = Number(input);
    }
    printNumber(number);
