function generateCleanContacts(rawData){
  let dirtyContacts = rawData.split(',');
  let cleanedContacts = [];

  for (let dirtyContact of dirtyContacts){
       let emailDomain = findEmailDomain(dirtyContact);
       let dirtyfullName = findFullName(dirtyContact);
       let dirtyFirstName = findFirstName(dirtyfullName);
       let dirtyLastName = findLastName(dirtyfullName);
       let cleanFirstName = cleanName(dirtyFirstName);
       let cleanLastName = cleanName(dirtyLastName);
       let email = generateEmail(cleanLastName,emailDomain);
       let cleanContact = `${cleanFirstName} ${cleanLastName} <${email}>`;
       cleanedContacts.push(cleanContact);
  }

  cleanedContacts.sort();
  
  return cleanedContacts;

  function findEmailDomain(startingString){
    let length = startingString.length;
    let atSignIndex = startingString.indexOf('@');
    let domain = startingString.slice(atSignIndex,length);
    let domainTrimmed = domain.trim();
    return domainTrimmed;
  }

  function findFullName(startingString){
    let atSignIndex = startingString.indexOf('@');
    let fullName = startingString.slice(0,atSignIndex);
    fullName = fullName.replaceAll('_',' ');
    fullName = fullName.replaceAll('-',' ');
    fullName = fullName.replaceAll('\n','');
    fullName = removeSpacesBeginning(fullName);
    fullName = removeSpacesEnd(fullName);
    return fullName;
  }

  function findFirstName(fullName){
    indexOfFirstSpace = fullName.indexOf(' ');
    let firstName = fullName.slice(0,indexOfFirstSpace);
    return firstName;
  }

  function findLastName(fullName){
    indexOfFirstSpace = fullName.indexOf(' ');
    let lastName = fullName.slice(indexOfFirstSpace,fullName.length);
    return lastName;
  }

  function cleanName(name){
    name = name.trim()
    name = name.toLowerCase();
    let firstLetter = name.charAt(0);
    firstLetter = firstLetter.toUpperCase();
    cleanedName = firstLetter + name.slice(1,name.length);
    return cleanedName;
  }

  function generateEmail(lastName, domain){
    lastName = lastName.toLowerCase();
    let email = lastName+domain;
    return email;
  }

  function removeSpacesBeginning(startingString){
    let numSpacesStart = 0;
    let charsStartingString = startingString.split('');
    for(let character of charsStartingString){
        if(character === ' '){
           numSpacesStart+=1;
        }
        if(character !== ' '){
          break;
        }
    }

    let startingStringRemovedFrontSpaces =  startingString.slice(numSpacesStart,startingString.length)   

    if(numSpacesStart === 0){
      return startingString
    } else {
      return startingStringRemovedFrontSpaces;
    }
  }

  function removeSpacesEnd(startingString){
    let numSpacesEnd = 0;
    let charsStartingString = startingString.split('');
    charsStartingString.reverse();
    for(let character of charsStartingString){
        if(character === ' '){
           numSpacesEnd+=1;
        }
        if(character !== ' '){
          break;
        }
    }

    let startingStringRemovedEndSpaces =  startingString.slice(0,startingString.length -numSpacesEnd )

    if(numSpacesEnd === 0){
      return startingString
    } else {
      return startingStringRemovedEndSpaces;
    }
  }
}

