        <lightning-layout horizontal-align="space" vertical-align="center">
            <lightning-layout-item padding="around-small" >
                <lightning-input
                    class="small-input"
                    type="number"
                    label="Enter number"
                    value={inputValue}
                    onchange={handleInputChange}>
                </lightning-input>
            </lightning-layout-item>
             <lightning-layout-item padding="around-small" >
                <lightning-input
                    class="small-input"
                    type="number"
                    label="Enter number"
                    value={inputValue2}
                    onchange={handleInputChange2}>
                </lightning-input>
            </lightning-layout-item>
        </lightning-layout>
        <lightning-button
            class="slds-p-around_medium slds-m-bottom_large"
            label="Sum"
            onclick={handleSum}>
        </lightning-button>
        <lightning-button
            class="slds-p-around_medium slds-m-bottom_large"
            label="Multiply"
            onclick={handleMultiply}>
        </lightning-button>
         <lightning-button
            class="slds-p-around_medium slds-m-bottom_large"
            label="Divide"
            onclick={handleDivide}>
        </lightning-button>
         <lightning-button
            class="slds-p-around_medium slds-m-bottom_large"
            label="Subtract"
            onclick={handleSubtract}>
        </lightning-button>

            <div class="slds-m-top_medium slds-m-bottom_medium slds-text-align_center">
                <p>Sum: {sum}</p>
                <p>Multiply: {multiply}</p>
                <p>Divide: {divide}</p>
                <p>Subtract: {subtract}</p>
            </div>
        <lightning-button
            class="slds-p-around_medium slds-m-bottom_large"
            label="Reset"
            onclick={handleReset}>
        </lightning-button>
    </lightning-card>
</template>


import { LightningElement } from 'lwc';

export default class Calculator extends LightningElement {
    inputValue = '';
    inputValue2 = '';
    sum = 0;
    multiply = 0;
    divide = 0;
    subtract = 0;

    handleInputChange(event) {
        this.inputValue = event.target.value;
    }
   
    handleInputChange2(event) {
        this.inputValue2 = event.target.value;
    }

    handleSum() {
        const num1 = parseFloat(this.inputValue);
        const num2 = parseFloat(this.inputValue2);
        
        if (!isNaN(num1) && !isNaN(num2)) {
            this.sum = num1 + num2;
        } else {
            this.sum = 'Invalid input';
        }
    }

     handleMultiply() {
        const num1 = parseFloat(this.inputValue);
        const num2 = parseFloat(this.inputValue2);
        
        if (!isNaN(num1) && !isNaN(num2)) {
            this.multiply = num1 * num2;
        } else {
            this.multiply = 'Invalid input';
        }
    }


     handleSubtract() {
        const num1 = parseFloat(this.inputValue);
        const num2 = parseFloat(this.inputValue2);
        
        if (!isNaN(num1) && !isNaN(num2)) {
            this.subtract = num1 - num2;
        } else {
            this.subtract = 'Invalid input';
        }
    }

    
     handleDivide() {
        const num1 = parseFloat(this.inputValue);
        const num2 = parseFloat(this.inputValue2);
        
        if (!isNaN(num1) && !isNaN(num2)) {
            this.divide = num1 / num2;
        } else {
            this.divide = 'Invalid input';
        }
    }

    handleReset() {
        this.inputValue = '';
        this.inputValue2 = '';
        this.sum = 0;
        this.multiply = 0;
        this.divide = 0;
        this.subtract = 0;
    }
}

.small-input {
    width: 80px; 
}

