function removeEmptySpaces (stringValue) {
  return stringValue != ``;
}

function DataCleaner (rawString) {
  let returnArray = [];
  let splitRawEntries = rawString.split(`,`);
  for (let dataEntry of splitRawEntries) {
    let newEntry = dataEntry.replaceAll(`\n`, ` `);
    newEntry = newEntry.replaceAll(`-`, ` `);
    newEntry = newEntry.replaceAll(`_`, ` `);
    newEntry = newEntry.replaceAll(`@`, ` `);
    newEntry = newEntry.trim();
    let splitEntry = newEntry.split(` `);
    splitEntry = splitEntry.filter(removeEmptySpaces);
    for (let dataPortion in splitEntry) {
      if (splitEntry[dataPortion].includes(`.`) == true) {
        splitEntry[dataPortion] = `<${splitEntry[1].toLowerCase()}@${splitEntry[2]}>`;
        
      } else {
        splitEntry[dataPortion] = splitEntry[dataPortion].charAt(0).toUpperCase() + splitEntry[dataPortion].slice(1).toLowerCase();
      }
    }
    newEntry = `${splitEntry[0]} ${splitEntry[1]} ${splitEntry[2]}`;
    returnArray.push(newEntry);
  }
  return returnArray;
}
let cleanedData = DataCleaner(rawData);
console.log(cleanedData);

function deepEqual (objLeft, objRight) {
	let objLeftType = typeof objLeft;
	let objRightType = typeof objRight;

	if (objLeft === null || objRight === null) {
		if (objLeft === null && objRight === null) {
			return true;
		} else {
			return false;
		}
	}

	if (objLeft === undefined || objRight === undefined) {
		if (objLeft === undefined && objRight === undefined) {
			return true;
		} else {
			return false;
		}
	}

	if (objLeftType === objRightType) {
		if (objLeftType !== `object` && objRightType !== `object`) {
			if (objLeft === objRight) {
				return true;
			} else {
				return false;
			}
		} else {
			let objLeftProperties = Object.getOwnPropertyNames(objLeft);
			let objLeftValues = Object.values(objLeft);
			let objRightProperties = Object.getOwnPropertyNames(objRight);
			let objRightValues = Object.values(objRight);

            let largerStructure = objLeftProperties.length >= objRightProperties.length ? objLeftProperties : objRightProperties;
			let smallerStructure = objLeftProperties.length >= objRightProperties.length ? objRightProperties : objLeftProperties;
            let largerStructureValues = objLeftProperties.length >= objRightProperties.length ? objLeftValues : objRightValues;
            let smallerStructureValues = objLeftProperties.length >= objRightProperties.length ? objRightValues : objLeftValues;

            let nameAndValueTruthArray = [];
			for (let child of objLeftProperties) {
				let childLeftIndex = largerStructure.indexOf(child);
				let childLeftName = largerStructure[childLeftIndex];
				let childLeftValue = String.toString(largerStructureValues[childLeftIndex]);
				let childRightName = smallerStructure[childLeftIndex];
				let childRightValue = String.toString(smallerStructureValues[childLeftIndex]);
				if (childLeftName === childRightName && childLeftValue === childRightValue) {
					nameAndValueTruthArray.push(true);
				} else {
					nameAndValueTruthArray.push(false);
				}
			}
            let deepTruthArray = [];	
			if (nameAndValueTruthArray.indexOf(false) === -1) {
				let largerChildStructure = objLeftValues.length >= objRightValues.length ? objLeftValues : objRightValues;
				let smallerChildStructure = objLeftValues.length >= objRightValues.length ? objRightValues : objLeftValues;
				for (let valueChild of largerChildStructure) {
					let valueChildIndex = largerChildStructure.indexOf(valueChild);
					console.log(largerChildStructure[valueChildIndex]);
					console.log(smallerChildStructure[valueChildIndex]);
					deepTruthArray.push(deepEqual(largerChildStructure[valueChildIndex], smallerChildStructure[valueChildIndex]));
				}
				if (deepTruthArray.indexOf(false) === -1) {
                	return true;
            	} else {
                	return false;
            	}
			} else {
				return false;
			}
		}	
	} else {
		return false;
	}
}

console.log(deepEqual(1, 1));
console.log(deepEqual(1, '1'));
console.log(deepEqual(null, null));
console.log(deepEqual(null, {}));
console.log(deepEqual({a: 1}, {a: 1}));
console.log(deepEqual({a: 1}, {a: 2}));
console.log(deepEqual({a: 1}, {a: 1, b: 2}));
console.log(deepEqual({a: {b: 2}}, {a: {b: 2}}));
console.log(deepEqual({a: {b: 2}}, {a: {b: 3}}));

