function Week2Task6 () {
    function OneToHundredFizz(currentNumber) {
        //if first time being run in recursion, set to 1
        if (currentNumber === undefined) {
            currentNumber = 1;
        }
    
        if (currentNumber === 101) {
            return;
        } else {
            let modulo3 = currentNumber%3;
            let modulo5 = currentNumber%5;
            if (modulo3 === 0 && modulo5 === 0) {
                console.log(`FizzBuzz`);
            } else if (modulo3 === 0 && modulo5 !== 0) {
                console.log(`Fizz`);
            } else if (modulo3 !== 0 && modulo5 === 0) {
                console.log(`Buzz`);
            } else {
                console.log(currentNumber);
            }
            OneToHundredFizz(currentNumber + 1);
        }
    }
    OneToHundredFizz();
}
