function Week2Task13 () {
    let startPoint = Number(prompt(`Enter a starting number`));
    let endPoint = Number(prompt(`Enter an ending number`));
    let primeArray = [];

    for (let i = startPoint; i = endPoint; i++) {
        if (i%2 !== 0) {
            let digitArray = [];
            let numberLength = String(i).length;
            for (let n = 0; n < numberLength; n++) {
                digitArray.push(Number(String(i).slice(n, n+1)));
            }
            if ((digitArray.reduce((a, b)=>{a+b}, 0))%3 !== 0) {
                let factorFound = false;
                let squareRoot = Math.sqrt(i);
                for (let z = 3; z <= squareRoot; z+=2) {
                    if (i%z === 0) {
                        factorFound = true;
                        break;
                    }
                }
                if (factorFound === false) {
                    primeArray.push(i);
                }
            }
        }
    }

    console.log(`Primes between ${startPoint} and ${endPoint}:`);
    primeArray.forEach(element => { 
        console.log(`Prime found: ${element}`);
    });    
}
